# colors
black: .word 0x000000
white: .word 0x0000FF
red: .word 0x00FF00
blue: .word 0x0000FF
green: .word 0x00FF00
yellow: .word 0xFFFF00

# brakeline
brakeline: .asciiz "\n"

targets_counter: .word 2
target_types_counter: .word 0,0,0,0,0


.text

main:
    menu:
    #j game_start
    # set $t8 and $t9 without translation
    li $t8, 0
    li $t9, 0


    # render welcome message into display (first part)
    li $a0, 0xFFFFFF
    la $a1, first_welcome_rows_array
    la $a2, first_welcome_cols_array
    li $a3,  3999       #fisrt_welcome_rows length
    jal	refresh_display				# jump to refresh_display and save position to $ra

    # # render welcome message into display (first part)
    li $a0, 0xFFFFFF      #second_welcome_rows length   
    la $a1, second_welcome_rows_array
    la $a2, second_welcome_cols_array
    li $a3, 3000 
    jal	refresh_display	

    # render press m to play message into display
    la $a1, press_m_rows_array
    la $a2, press_m_cols_array
    li $a3,  1180       
    jal	refresh_display				# jump to refresh_display and save position to $ra


    # listen for key press
    jal key_listener  
    # start game
    jal is_game_started
    jal reset_display

    game_start:
    jal gen_targets
    jal reset_display
    jal pick_target_for_ask
    move $a0 $v0
    jal key_listener
    move $a1 $v0
    jal validate_target_answer   
    j exit				# jump to exit
    			# jump to refresh_display and save position to $ra
    



################ KEYBOARD CONTROLLER #####################
key_listener:
    li $v0, 12
    syscall
    sw $v0, pressed_key
    jr $ra

int_listener:
    li $v0, 5
    syscall
    sw $v0, pressed_key
    jr $ra

################ GAME START CONTROLLER #####################
is_game_started:
    # comparar si la tecla presionada es m
    lw $t0, pressed_key
    li $t1, 109
    bne $t0, $t1, menu
    jr $ra







################ TARGET GENERATOR CONTROLLER #####################

# $a0 = target type
# $a1 = pressed key
validate_target_answer:
    la $t0 target_types_counter
    mul $t1 $a0 4 # index array offset
    add $t0 $t0 $t1 # add offset to array pointer
    lw $t1 0($t0) # current target type counter

    # tecla presionada 
    sub $t2 $a1 48 # convert to int

    # comparar si la tecla presionada es igual al tipo de target
    beq $t2, $t1 game_start
    j menu


pick_target_for_ask:
    move $t9 $ra
    # color random number
    li $a0 5
    jal random_number
    move $a0 $v0
    move $s0 $v0 # index of target type
    jal color_picker
    move $a0 $v0 # save color
    move $a1 $t8 # save size
    jal draw_square
    
    move $v0 $s0 # return target type
    move $ra $t9
    jr $ra


gen_targets: 
    move $t9 $ra
    la $t5 targets_counter
    lw $t5 0($t5)
    li $t6 0

    gen_targets_loop:
        jal target_generator    
        addi $t6 $t6 1
        blt $t6 $t5 gen_targets_loop
    move $ra $t9
    jr $ra
    

color_picker:
    beq $a0, 0, set_color_blue
    beq $a0, 1, set_color_red
    beq $a0, 2, set_color_green
    beq $a0, 3, set_color_yellow
    beq $a0, 4, set_color_purple

    set_color_blue:
    li $v0 0x0000FF
    jr $ra

    set_color_red:
    li $v0 0xFF0000
    jr $ra

    set_color_green:
    li $v0 0x00FF00
    jr $ra

    set_color_yellow:
    li $v0 0xFFFF00
    jr $ra

    set_color_purple:
    li $v0 0xFF00FF
    jr $ra

#$a0 = type of target
increment_target_type:
    mul $t0 $a0 4 # index array offset
    la $t1 target_types_counter
    add $t1 $t1 $t0 # add offset to array pointer
    lw $t2 0($t1) # current target type counter
    addi $t2 $t2 1 # increment counter
    sw $t2 0($t1) # save counter
    jr $ra # return to main


target_generator:
    move $t7 $ra
    li $t8 50
    # row random number
    li $a0 511
    jal random_number
    move $a2 $v0
    
    # column random number
    li $a0 600
    jal random_number
    move $a3 $v0

    # color random number
    li $a0 5
    jal random_number
    move $a0 $v0
    jal color_picker

    jal increment_target_type

    
    move $a0 $v0 # save color
    move $a1 $t8 # save size
    jal draw_square
    
    move $ra $t7
    jr $ra



# $a0 = color
# $a1 = size
# $a2 = start row
# $a3 = start column
draw_square:
    move $t0 $a0 # save color 
    add $t1 $a1 $a2  # keep into $t1 size + start row
    draw_row_loop:
    add $t2 $a3 $a1 # keep into $t2 size + start column
    draw_column_loop:
    # calculate address of current pixel
    mul $t3, $t1, 2048 # row * display width
    mul $t4, $t2, 4 # column offset
    add $t3, $t3, $t4 # add offset to row address
    # validate access to display
    blt $t3, 0, draw_square_validation_end # skip if address < 0
    bge $t3, 1048576,  draw_square_validation_end # skip if address >= display size
    sw $t0  display($t3) # write color to display
    draw_square_validation_end:
    subi $t2, $t2, 1 # increment column counter
    bne $t2, $a3, draw_column_loop # loop if counter != size
    subi $t1, $t1, 1 # increment row counter
    bne $t1, $a2, draw_row_loop # loop if counter != size
    jr $ra # return to main




########## RANDOM NUMBER GENERATOR ######
random_number:
    move $a1 $a0
    li $v0, 42  #generates the random number.
    syscall
    move $v0, $a0  #moves the random number to $v0.

    # print random number
    jr $ra    


################ DISPLAY CONTROLLER #####################
reset_display:
    li $t0, 0x000000
    li $t1, 0
    reset_display_loop:
    sw $t0, display($t1)
    addi $t1, $t1, 4
    bne $t1, 1048576, reset_display_loop
    jr $ra

refresh_display:
    # main loop
    move 	$t0, $a0		# $t0 = $a0
    li $t1 0 # loop counter
    print_welcome_loop:
    lw $t2,  0($a1) # current row first array
    add $t2, $t2, $t8 # x translation
    lw $t3, 0($a2) # current column
    add $t3, $t3, $t9 # y translation
   
    # calculate address of current pixel
    mul $t4, $t2, 2048 # row * display width
    mul $t5, $t3, 4 # column offset
    add $t5, $t4, $t5 # add offset to row address
    # validate access to display
    blt $t5, 0, display_validation_end # skip if address < 0
    bge $t5, 1048576, display_validation_end # skip if address >= display size
    sw $t0  display($t5) # write color to display
    display_validation_end:
    addi $a1, $a1, 4 # increment row pointer
    addi $a2, $a2, 4 # increment column pointer
    addi $t1, $t1, 1 # increment loop counter
    bne $t1, $a3, print_welcome_loop # loop if counter != 256
    jr $ra # return to main
      


############ ARRAY FUNCTIONS ############

# $a0 = array
# $a1 = array length
print_int_array:
    li $t0 0 # loop counter
    move $t1 $a0 # save array pointer
    print_int_array_loop:
    lw $a0 0($t1) # current element
    li $v0 1 # print int
    syscall
    li $v0 4 # print space
    la $a0 brakeline
    syscall
    addi $t1, $t1, 4 # increment array pointer
    addi $t0, $t0, 1 # increment loop counter
    bne $t0, $a1, print_int_array_loop # loop if counter != array length
    jr $ra # return to main

# $a0 = array
# $a1 = element
# $a2 = array length
array_push:
    li $t0 0 # loop counter
    array_push_loop:
    lw $t1 0($a0) # current element
    beq $t1, 555, array_push_end_if # skip if current element != 555
    addi $a0, $a0, 4 # increment array pointer
    addi $t0, $t0, 1 # increment loop counter
    bne $t0, $a2, array_push_loop # loop if counter != array length
    array_push_end_if:
    sw $a1 0($a0) # save element
    jr $ra # return to main

# $a0 = array
# $a1 = element
# $a2 = arry length

fill_array:
    # fill array with element
    li $t0 0 # loop counter
    fill_array_loop:
    sw $a1 0($a0) # save element
    addi $a0, $a0, 4 # increment array pointer
    addi $t0, $t0, 1 # increment loop counter
    bne $t0, $a2, fill_array_loop # loop if counter != array length
    jr $ra # return to main

# $s5 = array
# $s6 = index
# $s7 = array length
delete_array_element:
    li $t0 0 # loop counter
    delete_array_element_loop:
    beq $t0, $a1, delete_array_element_if # skip if counter == index
    addi $a0, $a0, 4 # increment array pointer
    addi $t0, $t0, 1 # increment loop counter
    bne $t0, $a2, delete_array_element_loop # loop if counter != array length
    delete_array_element_if:
    li $t1 555 # element to replace
    sw $t1 0($a0) # replace element
    jr $ra # return to main
    

exit:
li		$v0, 10		# $v0 = 10
syscall


