################ KEYBOARD CONTROLLER #####################
key_listener:
    li $v0, 12
    syscall
    sw $v0, pressed_key
    jr $ra


################ GAME START CONTROLLER #####################
is_game_started:
    # comparar si la tecla presionada es m
    lw $t0, pressed_key
    li $t1, 109
    bne $t0, $t1, menu
    jr $ra

reset_arrays:
    move $t7 $ra
    la $a0 first_player_target_row_array
    li $a1 555
    li $a2 11
    jal fill_array
    la $a0 first_player_target_col_array
    jal fill_array
    la $a0 first_player_target_size_array
    jal fill_array
    la $a0 second_player_target_row_array
    jal fill_array
    la $a0 second_player_target_col_array
    jal fill_array
    la $a0 second_player_target_size_array
    jal fill_array
    move $ra $t7
    jr $ra

################ TURN CONTROLLER #####################
current_color:
    lw $t0, current_player
    li $t1, 0
    bne $t0, $t1, current_color_else
    li $v0, 0x0000FF
    j current_color_end_if
    current_color_else:
    li $v0, 0x00FF00
    current_color_end_if:
    jr $ra

change_player:
    lw $t0, current_player
    li $t1, 0
    bne $t0, $t1, change_player_else
    li $t0, 1
    sw $t0, current_player
    j change_player_end_if
    change_player_else:
    li $t0, 0
    sw $t0, current_player
    change_player_end_if:
    jr $ra    


################ TARGET GENERATOR CONTROLLER #####################
target_generator:
    move $t7 $ra
    move $t8 $a1 # save size
    li $a0 511
    jal random_number
    move $a2 $v0
    jal random_number
    move $a3 $v0
    jal current_color
    move $a0 $v0
    move $a1 $t8
    jal draw_square
    jal save_target
    move $ra $t7
    jr $ra

# a1 = size
#$a2 = row
#$a3 = column
save_target:
    move $t6 $ra
    lw $t0 current_player # get current player
    li $t1 0  # will be used to compare with current player
    bne $t0, $t1, save_second_player_target # if current player is 1, save target in second player array
    la $a0 first_player_target_size_array
    li $a3 11
    jal array_push
    la $a0 first_player_target_row_array
    move $a1 $a2
    jal array_push
    la $a0 first_player_target_col_array
    move $a1 $a3
    jal array_push
    la $t0 first_player_target_counter
    lw $t1 0($t0)
    addi $t1 $t1 1
    sw $t1 0($t0)
    move $ra $t6
    jr $ra
    save_second_player_target:
     la $a0 second_player_target_size_array
    li $a3 11
    jal array_push
    la $a0 second_player_target_row_array
    move $a1 $a2
    jal array_push
    la $a0 second_player_target_col_array
    move $a1 $a3
    jal array_push
    la $t0 second_player_target_counter
    lw $t1 0($t0)
    addi $t1 $t1 1
    sw $t1 0($t0)
    move $ra $t6
    jr $ra
    
   



     



ask_for_square_size:
    move $t7 $ra
    jal key_listener
    move $ra $t7
    ble $v0, 48, ask_for_square_size # if size <= 0, ask again
    bge $v0, 52, ask_for_square_size # if size >= 256, ask again

    bne $v0, 49, check_2_square_size
    li $v0 30
    j end_check_if
    check_2_square_size:
    bne $v0, 50, check_3_square_size
    li $v0 50
    j end_check_if
    check_3_square_size:
    li $v0 70

    end_check_if:
    jr $ra


# $a0 = color
# $a1 = size
# $a2 = start row
# $a3 = start column
draw_square:
    move $t0 $a0 # save color 
    add $t1 $a1 $a2  # keep into $t1 size + start row
    draw_row_loop:
    add $t2 $a3 $a1 # keep into $t2 size + start column
    draw_column_loop:
    # calculate address of current pixel
    mul $t3, $t1, 2048 # row * display width
    mul $t4, $t2, 4 # column offset
    add $t3, $t3, $t4 # add offset to row address
    # validate access to display
    blt $t3, 0, draw_square_validation_end # skip if address < 0
    bge $t3, 1048576,  draw_square_validation_end # skip if address >= display size
    sw $t0  display($t3) # write color to display
    draw_square_validation_end:
    subi $t2, $t2, 1 # increment column counter
    bne $t2, $a3, draw_column_loop # loop if counter != size
    subi $t1, $t1, 1 # increment row counter
    bne $t1, $a2, draw_row_loop # loop if counter != size
    jr $ra # return to main

# $a0 = color
# $a1 row array
# $a2 col array
# $a3 size array
print_player_targets:
    move $v0 $ra
    li $t5 0 # loop counter
    move $t7 $a1 # save row array pointer
    move $t8 $a2 # save col array pointer
    move $t9 $a3 # save size array pointer
    print_player_targets_loop:
    lw $t0, 0($t7) # current row
    lw $t1, 0($t8) # current column
    lw $t2, 0($t9) # current size
    li $t6 555
    beq $t0, $t6, print_player_targets_end_if # skip if current size == -1
    move $a0 $t0
    move $a1 $t1
    move $a2 $t2
    jal draw_square
    print_player_targets_end_if:
    addi $t7, $t7, 4 # increment row pointer
    addi $t8, $t8, 4 # increment column pointer
    addi $t9, $t9, 4 # increment size pointer
    addi $t5, $t5, 1 # increment loop counter
    bne $t5, 11, print_player_targets_loop # loop if counter != 256
    move $ra $v0
    jr $ra # return to main




   

