    add $t5, $t4, $t5 # add offset to row address
    sw $t0  display($t5) # write color to display
    addi $a1, $a1, 4 # increment row pointer
    addi $a2, $a2, 4 # increment column pointer
    addi $t1, $t1, 1 # increment loop counter
    bne $t1, $a3, print_welcome_loop # loop if counter != 256
    jr $ra # return to main
      




exit:
li		$v0, 10		# $v0 = 10
syscall